8\",","","    hl = \"en\",","","    dt = \"t\",","","    tl = target_lang or \"en\",","","    sl = source_lang or \"auto\",","","    text = URL.escape(text)","","  }","","","  local query = format_http_params(params, true)","","  local url = path..query","","","  local res, code = https.request(url)","","  -- Return nil if error","","  if code > 200 then return nil end","","  local trans = res:gmatch(\"%[%[%[\\\"(.*)\\\"\")():gsub(\"\\\"(.*)\", \"\")","","","  return trans","","end","","","function run(msg, matches)","","  -- Third pattern","","  if #matches == 1 then","","    print(\"First\")","","    local text = matches[1]","","    return translate(nil, nil, text)","","  end","","","  -- Second pattern","","  if #matches == 2 then","","    print(\"Second\")","","    local target = matches[1]","","    local text = matches[2]","","    return translate(nil, target, text)","","  end","","","  -- First pattern","","  if #matches == 3 then","","    print(\"Third\")","","    local source = matches[1]","","    local target = matches[2]","","    local text = matches[3]","","    return translate(source, target, text)","","  end","","","end","","","return {","","  description = \"Translate some text\", ","","  usage = {","","    \"!translate text. Translate the text to English.\",","","    \"!translate target_lang text.\",","","    \"!translate source,target text\",","","  },","","  patterns = {","","    \"^[!/$&-= :*.%#?@]translate ([%w] ),([%a] ) (. )\",","","    \"^[!/$&-= :*.%#?@]translate ([%w] ) (. )\",","","    \"^[!/$&-= :*.%#?@]translate (. )\",","","    \"^translate ([%w] ),([%a] ) (. )\",","","    \"^translate ([%w] ) (. )\",","","    \"^translate (. )\",","","  }, ","","  run = run ","","}","","","end","","--shared by @Mr_Vigeo"]}]]},"ace":{"folds":[],"scrolltop":918.1209564208984,"scrollleft":162,"selection":{"start":{"row":144,"column":21},"end":{"row":144,"column":21},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"